  render() {
    return(
      <div>
        {/* create lobby button */}
        {/* join lobby button */}
        <button onClick={()=>this.startGame(this.props.username)}>BEGIN</button>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    pubnub: state.connectionReducer.pubnub,
    username: state.playerReducer.username,
    isPlaying: state.gameReducer.isPlaying,

  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    startGame: (() => dispatch({type: 'startGame', username: this.props.username}))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Lobby)
