import React from 'react';
import Board from './Board';
import Swal from "sweetalert2";  
import { connect } from 'react-redux'

class Game extends React.Component {
  componentDidMount(){
    fetch('http://localhost:3000/deck')
    .then(r => r.json())
    .then(deck => {
      this.props.init(deck.cards)
    })
  }

  render() {
    let status;
    // Change to current player's turn
    status = `${this.state.whosTurn ? "Your turn" : "Opponent's turn"}`;

    return (
      <div className="game">
        <div className="board">
          <Board
              squares={this.state.squares}
              onClick={index => this.onMakeMove(index)}
            />  
            <p className="status-info">{status}</p>        
        </div>

        <div className="scores-container">
          <div>
            <p>Player X: {this.state.xScore} </p>
          </div>

          <div>
            <p>Player O: {this.state.oScore} </p>
          </div>
        </div>   
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    deck: state.deck
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    init: ((cards) => dispatch({type: 'initializeDeck', cards: cards}))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Game)
