const initialState = {
  deck: [],
  isPlaying: false,

}

export default function gameReducer(state = initialState, action) {
  switch(action.type) {
    case 'initializeDeck':
      return {
        deck: action.cards
      }
    case 'shuffleDeck':
      return state
    default:
      return state
  }
}
