import React from 'react';
import { connect } from 'react-redux'
import Game from '../components/Game';
import '../components/Game.css';
import Board from '../components/Board';
import PubNubReact from 'pubnub-react';
import Swal from "sweetalert2";  
// import shortid  from 'shortid';
import keys from '../PubNub-keys.js'

class GameContainer extends React.Component {
  constructor(props) {  
    super(props);
    // // REPLACE with your keys
    // this.props.pubnub = new PubNubReact({
    //   publishKey: keys.publishKey, 
    //   subscribeKey: keys.subscribeKey    
    // });

    this.state = {
      piece: '', // X or O
      isPlaying: false, // Set to true when 2 players are in a channel
      isRoomCreator: false,
      isDisabled: false,
      myTurn: false,
    };
    this.lobbyChannel = null; // Lobby channel
    this.gameChannel = null; // Game channel
    this.roomId = null; // Unique id when player creates a room   
    this.props.pubnub.init(this); // Initialize PubNub
  }

  componentWillUnmount() {
    this.props.pubnub.unsubscribe({
      channels : [this.lobbyChannel, this.gameChannel]
    });
  }

  componentDidUpdate() {
    // Check that the player is connected to a channel
    if(this.lobbyChannel != null){
      this.props.pubnub.getMessage(this.lobbyChannel, (msg) => {
        // Start the game once an opponent joins the channel
        if(msg.message.notRoomCreator){
          // Create a different channel for the game
          this.gameChannel = 'tictactoegame--' + this.roomId;

          this.props.pubnub.subscribe({
            channels: [this.gameChannel]
          });

          this.setState({
            isPlaying: true
          });  

          // Close the modals if they are opened
          Swal.close();
        }
      }); 
    }
  }

  // Create a room channel
  onPressCreate = (e) => {
    // Create a random name for the channel
    // this.roomId = shortid.generate().substring(0,5);
    this.roomId = Math.random().toString(36).substring(2,7).toUpperCase()
    this.lobbyChannel = 'tictactoelobby--' + this.roomId;

    this.props.pubnub.subscribe({
      channels: [this.lobbyChannel],
      withPresence: true
    });

    // Open the modal
    Swal.fire({
      position: 'top',
      allowOutsideClick: false,
      title: 'Share this room ID with your friend',
      text: this.roomId,
      width: 275,
      padding: '0.7em',
      // Custom CSS
      customClass: {
          heightAuto: false,
          title: 'title-class',
          popup: 'popup-class',
          confirmButton: 'button-class'
      }
    })

    this.setState({
      piece: 'X',
      isRoomCreator: true,
      isDisabled: true, // Disable the 'Create' button
      myTurn: true, // Room creator makes the 1st move
    });
  }

  // The 'Join' button was pressed
  onPressJoin = (e) => {
    Swal.fire({
      position: 'top',
      input: 'text',
      allowOutsideClick: false,
      inputPlaceholder: 'Enter the room id',
      showCancelButton: true,
      confirmButtonColor: 'rgb(208,33,41)',
      confirmButtonText: 'OK',
      width: 275,
      padding: '0.7em',
      customClass: {
        heightAuto: false,
        popup: 'popup-class',
        confirmButton: 'join-button-class ',
        cancelButton: 'join-button-class'
      } 
    }).then((result) => {
      // Check if the user typed a value in the input field
      if(result.value){
        this.joinRoom(result.value);
      }
    })
  }

  // Join a room channel
  joinRoom = (value) => {
    this.roomId = value;
    this.lobbyChannel = 'tictactoelobby--' + this.roomId;

    // Check the number of people in the channel
    this.props.pubnub.hereNow({
      channels: [this.lobbyChannel], 
    }).then((response) => { 
        if(response.totalOccupancy < 2){
          this.props.pubnub.subscribe({
            channels: [this.lobbyChannel],
            withPresence: true
          });

          this.setState({
            piece: 'O',
          });  

          this.props.pubnub.publish({
            message: {
              notRoomCreator: true,
            },
            channel: this.lobbyChannel
          });
        } 
        else{
          // Game in progress
          Swal.fire({
            position: 'top',
            allowOutsideClick: false,
            title: 'Error',
            text: 'Game in progress. Try another room.',
            width: 275,
            padding: '0.7em',
            customClass: {
                heightAuto: false,
                title: 'title-class',
                popup: 'popup-class',
                confirmButton: 'button-class'
            }
          })
        }
    }).catch((error) => { 
      console.log(error);
    });
  }

  // Reset everything
  endGame = () => {
    this.setState({
      piece: '',
      isPlaying: false,
      isRoomCreator: false,
      isDisabled: false,
      myTurn: false,
    });

    this.lobbyChannel = null;
    this.gameChannel = null;
    this.roomId = null;  

    this.props.pubnub.unsubscribe({
      channels : [this.lobbyChannel, this.gameChannel]
    });
  }

  render() {  
    return (  
        <div> 
          <div className="title">
            <p>React Tic Tac Toe</p>
            {this.props.username ? <p>Welcome, this.props.username!</p> : null}
          </div>

          {
            !this.state.isPlaying &&
            <div className="game">
              <div className="board">
                <Board
                    squares={0}
                    onClick={index => null}
                  />  

                <div className="button-container">
                  <button 
                    className="create-button "
                    disabled={this.state.isDisabled}
                    onClick={(e) => this.onPressCreate()}
                    > Create 
                  </button>
                  <button 
                    className="join-button"
                    onClick={(e) => this.onPressJoin()}
                    > Join 
                  </button>
                </div>                        

              </div>
            </div>
          }

          {
            this.state.isPlaying &&
            <Game 
              pubnub={this.props.pubnub}
              gameChannel={this.gameChannel} 
              piece={this.state.piece}
              isRoomCreator={this.state.isRoomCreator}
              myTurn={this.state.myTurn}
              xUsername={this.state.xUsername}
              oUsername={this.state.oUsername}
              endGame={this.endGame}
            />
          }
        </div>
    );  
  }
}

const mapStateToProps = (state) => {
  return {
    pubnub: state.pubnub,
    username: state.username
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    setUsername: ((username) => dispatch({type: 'setUsername', username: username}))
  }
}

export default connect(mapStateToProps)(GameContainer)
