const initialState = {
  id: NaN,
  username: 'LORD DINGO',
  isPlaying: false
}

export default function playerReducer(state = initialState, action) {
  switch(action.type) {
    case 'setUsername':
      return {
        ...state,
        username: action.username
      }
      case 'startGame':
        return {
          ...state,
          isPlaying: true
        }
    default:
      return state
  }
}
