import React from 'react'
import { connect } from 'react-redux'
import Opponent from '../components/Opponent.js'

class OpponentContainer extends React.Component {
  render() {
    const opponents = this.props.players.filter(player => player.id !== this.props.player.id)
    return(
      <div>
        {opponents.map(opponent => <Opponent opponent={opponent} />)}
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    player: state.playerReducer,
    players: state.gameReducer.players,
  }
}

export default connect(mapStateToProps)(OpponentContainer)
